dict.string:
$DEFAULT_CURRENCY = 'USD';

$SHOW_CREDITS = true;
$SHOW_HELP = true;

setlocale( LC_ALL, 'en_US.UTF-8' );

$json = file_get_contents( 'http://realtimebitcoin.info/stats' );
$stats = json_decode( $json );

$currency = strtoupper( trim( "{query}" ) );

if( !$currency || !isset( $stats->ticker->$currency ) ) {
    $currency = $DEFAULT_CURRENCY;
}

$symbol = $stats->ticker->$currency->symbol;
unset( $stats->ticker->$currency->symbol );

if( mb_strlen( $symbol, 'utf-8' ) > 1 ) {
    $symbol .= ' ';
}

$labels = array(
    'last' => 'Now',
    'buy'  => 'Buy',
    'sell' => 'Sell',
    '15m'  => '15m ago',
    '24h'  => '24h ago'
);

print '<?xml version="1.0"?>' . "\n";
print '<items>' . "\n";

foreach( $labels as $key => $label ) {
    $value = number_format( $stats->ticker->$currency->$key, 2, '.', '\'' );
    $value = $symbol . $value;
    print '  <item uid="btc-' . $key . '-' . uniqid() . '" arg="' . $value . '">' . "\n";
    print '    <title>' . $label . ': ' . $value . '</title>' . "\n";
    print '    <subtitle>Exchange rate</subtitle>' . "\n";
    print '    <icon>icon.png</icon>' . "\n";
    print '  </item>' . "\n";
}

$total = number_format( $stats->totalbtc, 0, '', '\'' );
print '  <item uid="btc-totalbtc-' . uniqid() . '" arg="' . $total . '">' . "\n";
print '    <title>' .$total  . '</title>' . "\n";
print '    <subtitle>Coins in circulation</subtitle>' . "\n";
print '    <icon>icon.png</icon>' . "\n";
print '  </item>' . "\n";

if( !empty( $SHOW_CREDITS ) ) {
	print '  <item uid="btc-credits-' . uniqid() . '" arg="http://realtimebitcoin.info">' . "\n";
	print '    <title>Credits</title>' . "\n";
	print '    <subtitle>Stats gathered from realtimebitcoin.info</subtitle>' . "\n";
	print '    <icon>icon.png</icon>' . "\n";
	print '  </item>' . "\n";
}

if( !empty( $SHOW_HELP ) ) {
	print '  <item uid="btc-help-' . uniqid() . '" valid="no">' . "\n";
	print '    <title>Help</title>' . "\n";
	print '    <subtitle>Press enter to copy any value above to clipboard</subtitle>' . "\n";
	print '    <icon>icon.png</icon>' . "\n";
	print '  </item>' . "\n";
}

/*
$currencies = implode( array_keys( ( array )$stats->ticker ), ', ' );
print '  <item uid="btc-currencies-' . uniqid() . '" arg="' . $currencies . '">' . "\n";
print '    <title>Copy available currencies</title>' . "\n";
print '    <subtitle>' . $currencies . '</subtitle>' . "\n";
print '    <icon>icon.png</icon>' . "\n";
print '  </item>' . "\n";
*/

echo '</items>' . "\n";
